				function VSacb_RGBtoHex($par)
				{
					if (stristr($par, "rgb"))
					{
						$trash = array('rgb(',')',' ');
						$req = str_ireplace($trash, "", $par);
						$val = explode(",", $req);
						$str = '';
						foreach($val as $c)
						{
							$str .= substr(sprintf('0%x',$c),-2);
						}
						return '#'.$str;
					}
					else
					{
						return $par;
					}
				}
			}

