					$vsacb_msg_arc_parsed = str_replace('\'', '&#39;', $vsacb_bbparser->do_parse($vsacb_msg_arc_styled,1,$vsacb_smilies_onoff,1,1,1));

					$templater = vB_Template::create('vsa_chatbox_archive_bit');
