					$templater->register('vsacb_msg_arc_icon', $vsacb_msg_arc_icon);
					$templater->register('vsacb_msg_arc_parsed', $vsacb_msg_arc_parsed);
					$templater->register('vsacb_msg_arc_staff', $vsacb_msg_arc_staff);
