					$templater->register('vsacb_canmod', $vsacb_canmod);
					$templater->register('vsacb_canedit', $vsacb_canedit);
					$templater->register('vsacb_totalentries', $vsacb_totalentries);
					$templater->register('vsacb_targettext', $vsacb_targettext);

